import React from 'react';
import { useForm } from "react-hook-form";

export default function CreateActivity() {

    const { register, handleSubmit, errors } = useForm();
    const onSubmit = data => console.log(data);

    return (
        <div className="wrapper" style={{marginLeft: '30%', marginRight: '30%', marginTop: '10px'}}>
            <form onSubmit={handleSubmit(onSubmit)}>
                <h1 style={{marginLeft: '25%', marginBottom: '20px'}}>New activity</h1>

                <label>Select lecture</label>
                <select name='lecture' className="form-control" ref={register({ required: true})}></select>
                {errors.lecture && <p style={{color: 'red', marginLeft: '5px'}}>Field required</p>}

                <label style={{marginTop: '10px'}}>Select teacher</label>
                <select name='teacher' className="form-control" ref={register({ required: true})}></select>
                {errors.lecture && <p style={{color: 'red', marginLeft: '5px'}}>Field required</p>}
            
                <label style={{marginTop: '10px'}}>Select group</label>
                <select name='group' className="form-control" ref={register({ required: true})}></select>
                {errors.lecture && <p style={{color: 'red', marginLeft: '5px'}}>Field required</p>}

                <input type="submit" value="Create activity" className="btn btn-success btn-block" style={{marginTop: '20px'}}/>                
            </form>
        </div>
    );
    
}
