import React, {useState, useEffect} from 'react'
import axios from "axios";
import ReactDataGrid from "react-data-grid";

const homeStyle = {
    display: 'grid',
    backgroundColor: 'rgba(0,0,0,0.1)',
    padding: '20px', 
}

const roomSelectStyle = {
    height: '10%'
}

const selectStyle = {    
    control: (base) => ({
        ...base,
        width: '150px'
    }),
    menu: (base) => ({
        ...base,
        width: '150px'
    })
}

const columns = [
    {name: 'Time', key: 'time', editable: false},
    {name: 'Monday', key: 'monday', editable: true},
    {name: 'Tuesday', key: 'tuesday', editable: true},
    {name: 'Wednesday', key: 'wednesday', editable: true},
    {name: 'Thursday', key: 'thursday', editable: true},
    {name: 'Friday', key: 'friday', editable: true}
]

const times2 = [
    {'time': '8:00 - 8:50'},  // slot 1
    {'time': '9:00 - 9:50'},
    {'time': '10:00 - 10:50'},
    {'time': '11:00 - 11:50'},
    {'time': '12:00 - 12:50'},
    {'time': '13:00 - 13:50'},  // slot 6
    {'time': '14:00 - 14:50'},
    {'time': '15:00 - 15:50'},
    {'time': '16:00 - 16:50'},
    {'time': '17:00 - 17:50'}  // slot 10 
]

const times = ['8:00 - 8:50',  // slot 1
    '9:00 - 9:50',
    '10:00 - 10:50',
    '11:00 - 11:50',
    '12:00 - 12:50',
    '13:00 - 13:50',  // slot 6
    '14:00 - 14:50',
    '15:00 - 15:50',
    '16:00 - 16:50',
    '17:00 - 17:50'  // slot 10 
]

const Home = () => {

    const [currentRoom, setCurrentRoom] = useState('120');
    const [rooms, setRooms] = useState([]);
    const [activities, setActivities] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                '/plannerData',
            );
            setRooms(result.data.rooms);
            setActivities(result.data.activities);
        }
        fetchData();
        console.log('fetching data...');
    }, []);

    const roomChanged = (room) => {
        console.log('room changed to ' + currentRoom + ' (currentRoom)');
        //setCurrentRoom(room);  // not working
        console.log('room changed to ' + currentRoom + ' (currentRoom)');
        console.log('room changed to ' + room + ' (room)');
    }
    
    const roomsSelect = (room) => {
        if(currentRoom == room){
            return <option selected>{room}</option>;
        }
        else{
            return <option>{room}</option>;
        }
    }

    return (
        <div style={{marginLeft: '10%', marginRight: '10%', marginTop: '10px'}}>
            <div className="wrapper">
                <form>
                    <div className="form-group">
                        <label>Room:</label>
                        <select className="form-control" value={currentRoom} onChange={roomChanged} style={{width: '150px'}}>
                            {rooms.map(roomsSelect)}
                        </select>
                    </div>
                </form>

                <ReactDataGrid columns={columns} rowGetter={(i) => times[i]} rowsCount={10} />

            </div>
            
            <ul style={{margin: '15px'}}>
                {activities.map((activity, index) => (
                <li key={index}>
                    <h3>{activity.class}: {activity.teacher}</h3>
                </li>
                ))}
            </ul>
        </div>    
    )
}

export default Home
