import React from 'react'
import Select from 'react-select'

const rooms = [
    {value: 110, label: "110"},
    {value: 111, label: "111"},
    {value: 120, label: "120"},
    {value: 121, label: "121"}
]

const selectStyle = {    
    control: (base) => ({
        width: '50%',
        boxShadow: 'none'
    }),
    menu: (base) => ({
        width: 80,
        textAlign: 'center'
    })
}

const Home = () => {
    return (
        <div style="width: 50%">
            <h3>Room</h3>
            <Select styles={selectStyle} options={rooms}></Select>

        </div>
    )
}

export default Home
