import React, {useState, useEffect} from 'react'
import axios from "axios";
import Select from 'react-select'

/*const rooms = [
    {value: 110, label: "110"},
    {value: 111, label: "111"},
    {value: 120, label: "120"},
    {value: 121, label: "121"}
]*/

const homeStyle = {
    display: 'grid',
    backgroundColor: 'rgba(0,0,0,0.1)',
    padding: '15px', 
    width: '100%',
    height: '100vh'
}

const roomSelectStyle = {
    height: '10%',
    display: 'flex'
}

const selectStyle = {    
    control: (base) => ({
        ...base,
        width: '150px'
    }),
    menu: (base) => ({
        ...base,
        width: '150px'
    })
}

const Home = () => {

    const [currentRoom, setCurrentRoom] = useState('');
    const [rooms, setRooms] = useState([]);
    const [activities, setActivities] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                '/plannerData',
            );
            setRooms(result.data.rooms);
            setActivities(result.data.activities);
        }
        fetchData();
        console.log('fetching data...');
    }, []);

    /*useEffect(() => {
        setCurrentRoom(Select.value);
        console.log('room changed to ' + currentRoom);
    }, []);*/

    const roomChanged = (room) => {
        setCurrentRoom('1999');  // not working
        console.log('room changed to ' + currentRoom);
    }

    const roomsSelect = rooms.map(t => ({ value: t, label: t}));

    return (
        <div style={homeStyle}>
            <div style={roomSelectStyle}>
                <h2 style={{alignContent: 'center', marginRight: '10px'}}>Room</h2>
                <Select styles={selectStyle} options={roomsSelect} value={currentRoom} onChange={roomChanged.bind(this)}
                placeholder='Select room'></Select>
            </div>
            
            <ul style={{padding: '15px'}}>
                {activities.map((activity, index) => (
                <li key={index}>
                    <h3>{activity.class}: {activity.teacher}</h3>
                </li>
                ))}
            </ul>
        </div>    
    )
}

export default Home
