import React, {useState, useEffect} from 'react'
import { axios } from "axios";
import Select from 'react-select'

/*const rooms = [
    {value: 110, label: "110"},
    {value: 111, label: "111"},
    {value: 120, label: "120"},
    {value: 121, label: "121"}
]*/

const homeStyle = {
    display: 'grid',
    backgroundColor: 'rgba(0,0,0,0.1)',
    padding: '15px', 
    width: '100%',
    height: '10%',
}

const selectStyle = {    
    control: (base) => ({
        ...base,
        width: '150px'
    }),
    menu: (base) => ({
        ...base,
        width: '150px'
    })
}

const Home = () => {

    const [rooms, setRooms] = useState({rooms: ['110','20']});
    /*useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                '/plannerData',
            );
            setRooms(result.data.rooms);
        }
        fetchData();
    }, []);*/

    const roomsSelect = Array.from(rooms).map(x => ({label: x, value: x}));
    console.log('roomsSelect:' + roomsSelect);

    return (
        <div style={homeStyle}>
            <Select styles={selectStyle} options={rooms} getOptionLabel={option => option} 
            getOptionValue={option => option} placeholder='Select room'></Select>
        </div>
    )
}

export default Home
