import React, { useState, useEffect, useMemo } from 'react'
import axios from "axios";
import { useForm } from "react-hook-form";
import { useHistory } from 'react-router-dom';
import { columns, timeSlots } from "./tableData";
import ReactTable, { useTable } from 'react-table';
import './tableStyle.css';

const Home = () => {

    const [rooms, setRooms] = useState([]);
    const [activities, setActivities] = useState([]);
    const {handleSubmit, register} = useForm();
    const [currentRoom, setCurrentRoom] = useState();

    useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                '/plannerData',
            );
            setRooms(result.data.rooms);
            setActivities(result.data.activities);
            if(currentRoom == undefined){
                setCurrentRoom(rooms[0]);
            }
        }
        fetchData();
        console.log('fetching data...');
    }, []);

    // CREATION OF THE TABLE
    const table = useTable({
        columns: useMemo(() => columns, []),
        data: useMemo(() => timeSlots, [])
    });

    const {getTableProps, getTableBodyProps, headerGroups, rows, prepareRow} = table;

    // ROOM
    const roomChanged = (data) => {
        setCurrentRoom(data['room']);

        // update activities
    }
    
    const roomsSelect = (room) => {
        return <option>{room}</option>;
    }

    const cellClicked = (state, rowInfo, column) => {

        console.log('cell clicked');
    }

    return (
        <div style={{marginLeft: '8%', marginRight: '8%', marginTop: '15px', marginBottom: '30px'}}>
            <div className="wrapper">

                <form onChange={handleSubmit(roomChanged)} style={{marginBottom: '30px'}}>
                    <label>Room:</label>
                    <select name='room' className="form-control" ref={register({ required: true})} style={{width: '150px'}}>
                        {rooms.map(roomsSelect)}
                    </select>       
                </form>

                <ReactTable getTrProps={(state, rowInfo, column) => cellClicked(state, rowInfo, column)}
                    columns={columns} data={timeSlots}/>




                

            </div>
        </div>    
    )
}

export default Home
