import React, {useState, useEffect} from 'react'
import axios from "axios";
import { useForm } from "react-hook-form";
import {useHistory} from 'react-router-dom';

// TABLE DATA
const columns = [
    {name: 'Time', key: 'time', editable: false},
    {name: 'Monday', key: 'monday', editable: true},
    {name: 'Tuesday', key: 'tuesday', editable: true},
    {name: 'Wednesday', key: 'wednesday', editable: true},
    {name: 'Thursday', key: 'thursday', editable: true},
    {name: 'Friday', key: 'friday', editable: true}
]

const times = [
    {'time': '8:00 - 8:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},  // slot 1
    {'time': '9:00 - 9:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '10:00 - 10:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '11:00 - 11:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '12:00 - 12:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '13:00 - 13:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},  // slot 6
    {'time': '14:00 - 14:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '15:00 - 15:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '16:00 - 16:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '17:00 - 17:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''}  // slot 10 
]

const Home = () => {

    const [rooms, setRooms] = useState([]);
    const [activities, setActivities] = useState([]);
    const {handleSubmit, register} = useForm();
    const [currentRoom, setCurrentRoom] = useState();

    useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                '/plannerData',
            );
            setRooms(result.data.rooms);
            setActivities(result.data.activities);
            if(currentRoom == undefined){
                setCurrentRoom(rooms[0]);
            }
        }
        fetchData();
        console.log('fetching data...');
    }, []);

    const roomChanged = (data) => {
        setCurrentRoom(data['room']);

        // update activities
    }
    
    const roomsSelect = (room) => {
        return <option>{room}</option>;
    }

    return (
        <div style={{marginLeft: '8%', marginRight: '8%', marginTop: '15px'}}>
            <div className="wrapper">

                <form onChange={handleSubmit(roomChanged)} style={{marginBottom: '30px'}}>
                    <label>Room:</label>
                    <select name='room' className="form-control" ref={register({ required: true})} style={{width: '150px'}}>
                        {rooms.map(roomsSelect)}
                    </select>       
                </form>

            </div>
        </div>    
    )
}

export default Home
