import React, { useState, useEffect, useMemo } from 'react'
import axios from "axios";
import { useForm } from "react-hook-form";
import { useHistory } from 'react-router-dom';
import { columns, tableData } from "./tableData";
import { useTable } from 'react-table';
import './tableStyle.css';

const Home = () => {

    const [rooms, setRooms] = useState([]);
    const [activities, setActivities] = useState([]);
    const {handleSubmit, register} = useForm();
    const [currentRoom, setCurrentRoom] = useState();
    const history = useHistory();
    const [rowsData, setRowsData] = useState(tableData);
    const [table, setTable] = useState();

    useEffect(() => {
        const getData = async () => {
            console.log('getting data...');
            const result = await axios.get('/plannerData');
            if(rooms.length == 0){
                setRooms(result.data.rooms);
                setActivities(result.data.activities);
            }
            if(currentRoom == undefined){
                setCurrentRoom(rooms[0]);
            }
        }
        getData();
    }, [rooms]);

    useEffect(() => {
        updateActivities();
    }, [currentRoom]);

    useEffect(() => {
        onFetchData();
    }, [rowsData]);

    const updateActivities = () => {

        console.log('updating activities...');
        console.log(activities);
        var rows = tableData; // reset

        if(currentRoom == undefined){
            setCurrentRoom(rooms[0]);
        }

        for(let i=0; i<activities.length; i++){
            var activity = activities[i];
            if(activity.room == currentRoom){
                var row = rows[activity.slot-1];
                console.log(row);
                row[activity['day']] = activity['class'];
                console.log(row);
                rows[activity.slot-1] = row;
                console.log(rows);
            }
        }
        setRowsData(rows);
        
    }

    // CREATION OF THE TABLE
    /*const table = useTable({
        columns: useMemo(() => columns, []),
        data: useMemo(() => rowsData, [])
    });*/

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow
    } = useTable({ columns: columns, data: rowsData });

    //const {getTableProps, getTableBodyProps, headerGroups, rows, prepareRow} = table;

    // ROOMS
    const roomChanged = (data) => {
        setCurrentRoom(data['room']);
    }
    
    const roomsSelect = (room) => {
        return <option>{room}</option>;
    }

    // CELL CLICKED
    const createActivity = (cell) => {

        console.log('cell clicked');
        console.log(cell.column);

        var info = {
            'room': currentRoom,
            'slot': cell.row.index + 1,
            'day': cell.column.id
        }
        console.log(info);

        history.push('/createActivity', info);
    }

    return (
        <div style={{marginLeft: '8%', marginRight: '8%', marginTop: '15px', marginBottom: '30px'}}>
            <div className="wrapper">

                <form onChange={handleSubmit(roomChanged)} style={{marginBottom: '30px'}}>
                    <label>Room:</label>
                    <select name='room' className="form-control" ref={register({ required: true})} style={{width: '150px'}}>
                        {rooms.map(roomsSelect)}
                    </select>       
                </form>

                <table {...getTableProps()}>
                    <thead>
                        {headerGroups.map((headerGroup) => (
                            <tr {...headerGroup.getHeaderGroupProps()}>
                                {headerGroup.headers.map((column) => (
                                    <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                                ))}
                            </tr>
                        ))}                        
                    </thead>
                    <tbody {...getTableBodyProps()}>
                        {rows.map((row) => {
                            prepareRow(row)
                            return(
                                <tr {...row.getRowProps()}>
                                    {row.cells.map((cell) => {
                                        return <td {...cell.getCellProps()} onDoubleClick={() => createActivity(cell)}>
                                            {cell.render('Cell')}</td>
                                    })}
                                    
                                </tr>
                            )
                        })}
                    </tbody>
                </table>

            </div>
        </div>    
    )
}

export default Home
