import React from 'react'
import Select from 'react-select'

const rooms = [
    {value: 110, label: "110"},
    {value: 111, label: "111"},
    {value: 120, label: "120"},
    {value: 121, label: "121"}
]

const homeStyle = {
    display: 'grid',
    backgroundColor: 'rgba(0,0,0,0.1)',
    padding: '15px', 
    width: '100%',
    height: '10%',

}

const selectStyle = {    
    control: (base) => ({
        ...base,
        width: '100px'
    }),
    menu: (base) => ({
        ...base,
        width: '50px',
    })
}

const Home = () => {
    return (
        <div style={homeStyle}>
            <h3>Room</h3>
            <Select options={rooms} placeholder='Select room'></Select>
        </div>
    )
}

export default Home
