import React, {useState, useEffect} from 'react'
import axios from "axios";
import Select from 'react-select'

const homeStyle = {
    display: 'grid',
    backgroundColor: 'rgba(0,0,0,0.1)',
    padding: '20px', 
}

const roomSelectStyle = {
    height: '10%'
}

const selectStyle = {    
    control: (base) => ({
        ...base,
        width: '150px'
    }),
    menu: (base) => ({
        ...base,
        width: '150px'
    })
}

const columns = [
    {Header: 'Time', accessor: 'time'},
    {Header: 'Monday', accessor: 'monday'},
    {Header: 'Tuesday', accessor: 'tuesday'},
    {Header: 'Wednesday', accessor: 'wednesday'},
    {Header: 'Thursday', accessor: 'thursday'},
    {Header: 'Friday', accessor: 'friday'}
]

const Home = () => {

    const [currentRoom, setCurrentRoom] = useState('');
    const [rooms, setRooms] = useState([]);
    const [activities, setActivities] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                '/plannerData',
            );
            setRooms(result.data.rooms);
            setActivities(result.data.activities);
        }
        fetchData();
        console.log('fetching data...');
    }, []);

    /*useEffect(() => {
        setCurrentRoom(Select.value);
        console.log('room changed to ' + currentRoom);
    }, []);*/

    const roomChanged = (room) => {
        setCurrentRoom(room);  // not working
        console.log('room changed to ' + currentRoom);
    }

    const roomsSelect2 = rooms.map(t => ({ value: t, label: t}));

    const roomsSelect = (room) => {
        if(currentRoom == room){
            return <option selected>{room}</option>;
        }
        else{
            return <option>{room}</option>;
        }
    }

    return (
        <div>
            <div className="wrapper" style={{marginLeft: '10%', marginRight: '10%', marginTop: '10px'}}>
                <form>
                    <div className="form-group">
                        <label>Room:</label>
                        <select className="form-control" value={currentRoom} onChange={roomChanged} style={{width: '150px'}}>
                            {rooms.map(roomsSelect)}
                        </select>
                    </div>
                </form>
            </div>

            <div style={roomSelectStyle}>
                <h4 style={{marginLeft: '3px', marginBottom: '5px'}}>Room:</h4>
                <Select styles={selectStyle} options={roomsSelect2} value={currentRoom} onChange={roomChanged.bind(this)}
                placeholder='Select room'></Select>
            </div>
            
            <ul style={{padding: '15px'}}>
                {activities.map((activity, index) => (
                <li key={index}>
                    <h3>{activity.class}: {activity.teacher}</h3>
                </li>
                ))}
            </ul>
        </div>    
    )
}

export default Home
