import React, {useState, useEffect} from 'react'
import axios from "axios";
import ReactDataGrid from "react-data-grid";
import {useHistory} from 'react-router-dom';

const homeStyle = {
    display: 'grid',
    backgroundColor: 'rgba(0,0,0,0.1)',
    padding: '20px', 
}

const roomSelectStyle = {
    height: '10%'
}

const selectStyle = {    
    control: (base) => ({
        ...base,
        width: '150px'
    }),
    menu: (base) => ({
        ...base,
        width: '150px'
    })
}

const columns = [
    {name: 'Time', key: 'time', editable: false},
    {name: 'Monday', key: 'monday', editable: true},
    {name: 'Tuesday', key: 'tuesday', editable: true},
    {name: 'Wednesday', key: 'wednesday', editable: true},
    {name: 'Thursday', key: 'thursday', editable: true},
    {name: 'Friday', key: 'friday', editable: true}
]

const times = [
    {'time': '8:00 - 8:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},  // slot 1
    {'time': '9:00 - 9:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '10:00 - 10:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '11:00 - 11:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '12:00 - 12:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '13:00 - 13:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},  // slot 6
    {'time': '14:00 - 14:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '15:00 - 15:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '16:00 - 16:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''},
    {'time': '17:00 - 17:50', 'monday': '', 'tuesday': '', 'wednesday': '', 'thursday': '', 'friday': ''}  // slot 10 
]

const times2 = [
    '8:00 - 8:50',  // slot 1
    '9:00 - 9:50',
    '10:00 - 10:50',
    '11:00 - 11:50',
    '12:00 - 12:50',
    '13:00 - 13:50',  // slot 6
    '14:00 - 14:50',
    '15:00 - 15:50',
    '16:00 - 16:50',
    '17:00 - 17:50'  // slot 10 
]

const Home = () => {

    const [currentRoom, setCurrentRoom] = useState('120');
    const [rooms, setRooms] = useState([]);
    const [activities, setActivities] = useState([]);
    const [table, setTable] = useState(times);
    const history = useHistory();

    useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                '/plannerData',
            );
            setRooms(result.data.rooms);
            setActivities(result.data.activities);
        }
        fetchData();
        console.log('fetching data...');
    }, []);

    const roomChanged = (room) => {
        console.log('room changed to ' + currentRoom + ' (currentRoom)');
        //setCurrentRoom(room);  // not working
        console.log('room changed to ' + currentRoom + ' (currentRoom)');
        console.log('room changed to ' + room + ' (room)');
    }
    
    const roomsSelect = (room) => {
        if(currentRoom == room){
            return <option selected>{room}</option>;
        }
        else{
            return <option>{room}</option>;
        }
    }

    const tableUpdated = ({fromRow, toRow, updated}) => {
        setTable(table => {
            const rows = table.slice();
            for(let i=fromRow; i <= toRow; i++){
                rows[i] = {...rows[i], ...updated};
            }
            return {rows};
        });
    }

    const cellClicked = () => {
        console.log('Cell double clicked');
        history.push('/createActivity');
    }

    return (
        <div style={{marginLeft: '8%', marginRight: '8%', marginTop: '10px'}}>
            <div className="wrapper">
                <form>
                    <div className="form-group">
                        <label>Room:</label>
                        <select className="form-control" value={currentRoom} onChange={roomChanged} style={{width: '150px'}}>
                            {rooms.map(roomsSelect)}
                        </select>
                    </div>
                </form>

                <ReactDataGrid columns={columns} rows={table} rowsCount={10} onGridRowsUpdated={tableUpdated} 
                   onRowClick={cellClicked}/>

            </div>
        </div>    
    )
}

export default Home
