import React, {useState, useEffect} from 'react'
import axios from "axios";
import Select from 'react-select'

/*const rooms = [
    {value: 110, label: "110"},
    {value: 111, label: "111"},
    {value: 120, label: "120"},
    {value: 121, label: "121"}
]*/

const homeStyle = {
    display: 'grid',
    backgroundColor: 'rgba(0,0,0,0.1)',
    padding: '15px', 
    width: '100%',
    height: '10%',
}

const selectStyle = {    
    control: (base) => ({
        ...base,
        width: '150px'
    }),
    menu: (base) => ({
        ...base,
        width: '150px'
    })
}

const Home = () => {

    const [currentRoom, setCurrentRoom] = useState('');
    const [rooms, setRooms] = useState([]);
    const [activities, setActivities] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                '/plannerData',
            );
            setRooms(result.data.rooms);
            setActivities(result.data.activities);
        }
        fetchData();
    }, []);

    useEffect(() => {
        if(currentRoom != Select.value){
            setCurrentRoom(Select.value);
            console.log('room changed to ' + currentRoom);
        }        
    }, []);

    const roomsSelect = rooms.map(t => ({ value: t, label: t}));

    const roomChanged = (room) => {
        setCurrentRoom(room.value);
        console.log('room changed to ' + currentRoom);
    }

    return (
        <div style={homeStyle}>
            <Select styles={selectStyle} options={roomsSelect} defaultValue={roomsSelect[0]}
             placeholder='Select room'></Select>

            <ul>
                {activities.map(activity => (
                <li>
                    <h3>{activity.class}: {activity.teacher}</h3>
                </li>
                ))}
            </ul>

        </div>
    )
}

export default Home
