import React, {useState, useEffect, Component} from 'react'
import axios from "axios";

export default class Home extends Component {

    constructor(props){
        [currentRoom, setCurrentRoom] = useState('120');
        [rooms, setRooms] = useState([]);
        [activities, setActivities] = useState([]);

        useEffect(() => {
            const fetchData = async () => {
                const result = await axios(
                    '/plannerData',
                );
                setRooms(result.data.rooms);
                setActivities(result.data.activities);
            }
            fetchData();
            console.log('fetching data...');
        }, []);

        this.roomChanged = this.roomChanged.bind(this);
    }

    roomChanged = (room) => {
        console.log('room changed to ' + currentRoom + ' (currentRoom)');
        setCurrentRoom(room);  // not working
        console.log('room changed to ' + currentRoom + ' (currentRoom)');
        console.log('room changed to ' + room + ' (room)');
    }

    roomsSelect = (room) => {
        if(currentRoom == room){
            return <option selected>{room}</option>;
        }
        else{
            return <option>{room}</option>;
        }
    }

    homeStyle = {
        display: 'grid',
        backgroundColor: 'rgba(0,0,0,0.1)',
        padding: '20px', 
    }
    
    roomSelectStyle = {
        height: '10%'
    }
    
    selectStyle = {    
        control: (base) => ({
            ...base,
            width: '150px'
        }),
        menu: (base) => ({
            ...base,
            width: '150px'
        })
    }

    render(){
        return (
                <div style={{marginLeft: '10%', marginRight: '10%', marginTop: '10px'}}>
                    <div className="wrapper">
                        <form>
                            <div className="form-group">
                                <label>Room:</label>
                                <select className="form-control" value={currentRoom} onChange={roomChanged} style={{width: '150px'}}>
                                    {rooms.map(roomsSelect)}
                                </select>
                            </div>
                        </form>
                    </div>
                    
                    <ul style={{margin: '15px'}}>
                        {activities.map((activity, index) => (
                        <li key={index}>
                            <h3>{activity.class}: {activity.teacher}</h3>
                        </li>
                        ))}
                    </ul>
                </div>    
            )
    }
    
}
