import React, {useState, useEffect} from 'react'
import Select from 'react-select'



/*const rooms = [
    {value: 110, label: "110"},
    {value: 111, label: "111"},
    {value: 120, label: "120"},
    {value: 121, label: "121"}
]*/

const homeStyle = {
    display: 'grid',
    backgroundColor: 'rgba(0,0,0,0.1)',
    padding: '15px', 
    width: '100%',
    height: '10%',
}

const selectStyle = {    
    control: (base) => ({
        ...base,
        width: '150px'
    }),
    menu: (base) => ({
        ...base,
        width: '150px'
    })
}

const Home = ({rooms}) => {

    const [data, setData] = useState({});
    useEffect(() => {
        const fetchData = async () => {
        const result = await axios(
            '/plannerData'
        );
        setData(result.data.rooms);
        }
        fetchData();
    }, []);

    return (
        <div style={homeStyle}>
            <Select styles={selectStyle} options={rooms} placeholder='Select room'></Select>
        </div>
    )
}

export default Home
